package projectspring.pokedex.configuration;import org.modelmapper.ModelMapper;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import springfox.documentation.builders.ApiInfoBuilder;import springfox.documentation.builders.PathSelectors;import springfox.documentation.builders.RequestHandlerSelectors;import springfox.documentation.service.ApiInfo;import springfox.documentation.spi.DocumentationType;import springfox.documentation.swagger2.annotations.EnableSwagger2;import springfox.documentation.spring.web.plugins.Docket;@EnableSwagger2@Configurationpublic class PokedexConfiguration {    @Bean    public ModelMapper modelMapper() {        return new ModelMapper();    }    @Bean    public Docket apiDocket() {        return new Docket(DocumentationType.SWAGGER_2)                .select()                .apis(RequestHandlerSelectors.basePackage("projectspring.pokedex.controller"))                .paths(PathSelectors.any())                .build()                .apiInfo(getInfo());    }    private ApiInfo getInfo() {        return new ApiInfoBuilder()                .title("Pokedex api Doc")                .version("1.0")                .build();    }}