package projectspring.pokedex.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import projectspring.pokedex.model.dto.PokemonDto;import projectspring.pokedex.service.PokemonService;import java.util.List;@RestController@RequestMapping("/api/pokemons")public class PokemonController {    @Autowired    private PokemonService pokemonService;    @GetMapping    public List<PokemonDto> findAll() {        return pokemonService.findAll();    }    @GetMapping("/{id}")    public PokemonDto findById(@PathVariable Long id) {        return pokemonService.findById(id);    }    @PostMapping    public PokemonDto save(@RequestBody PokemonDto pokemonDto) {        return pokemonService.save(pokemonDto);    }    @PutMapping("/{id}")    public PokemonDto update(@PathVariable Long id, @RequestBody PokemonDto pokemonDto) {        return pokemonService.update(id, pokemonDto);    }    @DeleteMapping("/{id}")    public PokemonDto delete(@PathVariable Long id) {        return pokemonService.delete(id);    }}