package projectspring.pokedex.implementation;import org.modelmapper.ModelMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import projectspring.pokedex.model.dto.PokemonDto;import projectspring.pokedex.model.entity.Pokemon;import projectspring.pokedex.repository.PokemonRepository;import projectspring.pokedex.service.PokemonService;import java.util.List;import java.util.stream.Collectors;@Servicepublic class PokemonServiceImpl implements PokemonService {    static final String ENABLED = "ENABLED";    @Autowired    private PokemonRepository pokemonRepository;    @Autowired    private ModelMapper modelMapper;    private PokemonDto convertToDto(Pokemon pokemon) {        return modelMapper.map(pokemon, PokemonDto.class);    }    private Pokemon convertToEntity(PokemonDto pokemonDto) {        return modelMapper.map(pokemonDto, Pokemon.class);    }    @Override    public List<PokemonDto> findAll() {        List<Pokemon> pokemons = pokemonRepository.findByStatus(ENABLED);        return pokemons.stream()                .map(this::convertToDto)                .collect(Collectors.toList());    }    @Override    public PokemonDto findById(Long id) {        return convertToDto(pokemonRepository.findByIdAndStatus(id, ENABLED));    }    @Override    public PokemonDto save(PokemonDto pokemonDto) {        pokemonDto.setStatus(ENABLED);        Pokemon pokemon = convertToEntity(pokemonDto);        Pokemon insert = pokemonRepository.save(pokemon);        return convertToDto(insert);    }    @Override    public PokemonDto update(Long id, PokemonDto pokemonDto) {        Pokemon pokemon = pokemonRepository.findByIdAndStatus(id, ENABLED);        pokemon.setName(pokemonDto.getName());        pokemon.setType(pokemonDto.getType());        pokemon.setPower(pokemonDto.getPower());        Pokemon edit = pokemonRepository.save(pokemon);        return convertToDto(edit);    }    @Override    public PokemonDto delete(Long id) {        Pokemon pokemon = pokemonRepository.findByIdAndStatus(id, ENABLED);        pokemon.setStatus("DISABLED");        Pokemon drop = pokemonRepository.save(pokemon);        return convertToDto(drop);    }}