package projectspring.pokedex.repository;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import projectspring.pokedex.model.entity.Pokemon;import java.util.List;@Repositorypublic interface PokemonRepository extends JpaRepository<Pokemon, Long> {    List<Pokemon> findByStatus(String status);    Pokemon findByIdAndStatus(Long id, String status);}